import UIKit
import ZeusUtils

class ZeusBlogInteractor: ZeusBlogInteractorInputProtocol {
    weak var presenter: ZeusBlogInteractorOutputProtocol?
    
    func getURL() {
        let url = URL(string: "https://talentozeus.com/blog")
        if !ZWSConnectionChecker.networkIsAvailable() {
            presenter?.setError(errorType: .errorInternet)
            return
        }
        presenter?.setURL(url)
    }
}
