import UIKit

class ZeusBlogRouter: ZeusBlogWireframeProtocol {
    weak var viewController: UIViewController?

    static func createModule() -> UIViewController {
        let view = ZeusBlogViewController()
        let interactor = ZeusBlogInteractor()
        let router = ZeusBlogRouter()
        let presenter = ZeusBlogPresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view

        return view
    }
}
