import UIKit

class VerificationCodeRouter: VerificationCodeWireframeProtocol {
    weak var viewController: UIViewController?

    static func createModule(number: String, delegate: VerificationCodeDelegate) -> UIViewController {
        let view = VerificationCodeViewController()
        let interactor = VerificationCodeInteractor()
        let router = VerificationCodeRouter()
        let presenter = VerificationCodePresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view

        view.number = number
        view.delegate = delegate
        return view
    }
}
